import java.util.*;public class UserService {    private Map<Integer, User> users = new HashMap<>();    public void addUser(User user) {        users.put(user.getId(), user);    }    public User getUser(int id) {        return users.get(id);    }    public List<User> getAllUsers() {        return new ArrayList<>(users.values());    }    public void updateUser(int id, User updatedUser) {        if (users.containsKey(id)) {            users.put(id, updatedUser);        }    }    public void deleteUser(int id) {        users.remove(id);    }}
